import { Component } from '@angular/core';
import { FormBuilder} from "@angular/forms";

@Component({
  selector: 'app-user-add',
  templateUrl: './user-add.component.html',
  styleUrls: ['./user-add.component.css']
})
export class UserAddComponent {
  mailingListForm = this.formBuilder.group({
    firstName: "",
    lastName: "",
    email: ""
  })
  constructor(private formBuilder: FormBuilder) {}

  submitted = false;

  toggleSubmitted() {
    this.submitted = !this.submitted;
  }

  onSubmit(): void {
    this.toggleSubmitted();
    // add code here to hit api through a service that is set up with api calls
    this.mailingListForm.reset();
  }
}


<mat-card  class="MailingListAddCard">
  <mat-card-header>
    <mat-card-title class="white-text">Add me to the mailing list!</mat-card-title>
    <mat-card-subtitle>Input your information below</mat-card-subtitle>
  </mat-card-header>
  <mat-card-content>
    <form class="inputForm" [formGroup]="mailingListForm" *ngIf="!submitted">
      <mat-form-field class="full-width">
        <mat-label>
          First Name
        </mat-label>
        <input matInput>
      </mat-form-field>
      <mat-form-field class="full-width">
        <mat-label>
          Last Name
        </mat-label>
        <input matInput>
      </mat-form-field>
      <mat-form-field class="full-width">
        <mat-label>
          Email
        </mat-label>
        <input matInput>
      </mat-form-field>
      <button mat-raised-button color="primary" type="submit">Add me</button>
    </form>
    <p class="white-text" *ngIf="submitted">
      Your Information has been submitted.
    </p>


  </mat-card-content>
  <mat-card-actions align="end">

    <button mat-raised-button color="primary" (click)="toggleSubmitted()" *ngIf="submitted">Add Another</button>
  </mat-card-actions>
</mat-card>
